                // Create package
                in_toto_wrap(['stepName': 'Package',
                'keyPath': '/keys/carl',
                'transport': 'redis://']){
                    sh label: 'compress-for-release', script: 'tar --exclude .git -zcvf demo-project.tar.gz demo-project'
                }
            }
        }
    }
}
